import React from "react";
import {
  StyleSheet,
  View,
  TextInput,
  Text,
  Dimensions,
  Picker,
  Button,
} from "react-native";
import colors from "../config/colors";
import { Footer } from "../components/Footer";
import { Ionicons } from "@expo/vector-icons";

// Journal entry component
export class Journal extends React.Component {
  render() {
    return (
      <View>
        <TextInput
          style={styles.entry}
          multiline
          placeholder="Tell me about your day."
          placeholderTextColor="#000"
        />
        <Button title="Submit" color={colors.primary} />
      </View>
    );
  }
}

// Mood component
export class Mood extends React.Component {
  render() {
    return (
      <View>
        <Text style={styles.subheading}>{this.props.subheading}</Text>
        <View style={{ flexDirection: "row", alignItems: "center" }}>
          <View
            style={[styles.circle, { backgroundColor: this.props.bgcolor }]}
          ></View>
          <Text
            style={{
              borderRadius: 10,
              padding: 5,
            }}
          >
            {this.props.mood}
          </Text>
        </View>
        <View>
          <Text>{this.props.time}</Text>
          <Text>{this.props.length}</Text>
        </View>
      </View>
    );
  }
}

function JournalScreen({ route }) {
  const { date } = route.params;
  const [selectedValue, setSelectedValue] = React.useState("");
  const moodColor = !selectedValue ? colors.white : colors[selectedValue];
  return (
    <View style={styles.container}>
      {/* <View>
        <View style={{ flexDirection: "row", justifyContent: "space-around" }}>
          <FontAwesome name="chevron-left" size={24} color={colors.primary} />
          <FontAwesome name="chevron-right" size={24} color={colors.primary} />
        </View>
      </View> */}
      <View>
        <Text style={styles.dateHeading}>{date}</Text>
      </View>
      <View style={styles.layout}>
        <View style={styles.moodContainer}>
          <Mood subheading="My Mood" bgcolor={moodColor} mood={selectedValue} />
          <View style={{ backgroundColor: "black" }}>
            <Picker
              style={{ backgroundColor: "black" }}
              mode="dropdown"
              selectedValue={selectedValue}
              onValueChange={(itemValue) => setSelectedValue(itemValue)}
              prompt="Mood"
            >
              <Picker.Item label="Mood" value="" />
              <Picker.Item label="sad" value="sad" />
              <Picker.Item label="happy" value="happy" />
              <Picker.Item label="angry" value="angry" />
            </Picker>
          </View>
        </View>

        <View style={styles.moodContainer}>
          <Mood
            subheading="Audio (1)"
            bgcolor={colors.angry}
            mood="Angry"
            time="9:48PM"
            length="3 minutes"
          />
        </View>
      </View>
      <View>
        <Ionicons
          style={styles.notification}
          name="ios-notifications"
          size={40}
          color={colors.black}
        />
        <Journal style={{ alignSelf: "stretch" }} />
      </View>
      <Footer />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    flex: 1,
    justifyContent: "space-between",
    backgroundColor: colors.background,
  },
  mood: {
    flexDirection: "row",
  },
  moodContainer: {
    alignItems: "center",
    backgroundColor: colors.secondary,
    borderRadius: 10,
    width: "40%",
    padding: 10,
  },
  notification: {
    position: "absolute",
    alignSelf: "flex-end",
    padding: 10,
    zIndex: 1,
  },
  dateHeading: {
    backgroundColor: colors.primary,
    borderRadius: 10,
    color: "#fff",
    fontWeight: "bold",
    fontSize: 20,
    padding: 10,
  },
  entry: {
    backgroundColor: colors.secondary,
    minHeight: Dimensions.get("window").height * 0.2,
    borderRadius: 10,
    padding: 10,
  },
  subheading: {
    color: colors.primary,
    fontWeight: "bold",
  },
  layout: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-around",
  },
  circle: {
    borderRadius:
      Math.round(
        Dimensions.get("window").width + Dimensions.get("window").height
      ) / 2,
    width: Dimensions.get("window").width * 0.08,
    height: Dimensions.get("window").width * 0.08,
    margin: 5,
  },
});

export default JournalScreen;
